import{_ as l,r as s,o as h,c,a as t,b as o,w as r,F as d,e as a,d as e}from"./app.4f6a4bec.js";import{_ as p,a as m,b as u,c as f,d as g,e as b,f as w,g as _,h as y,i as n,j as k,k as x,l as v,m as T,n as S,o as C,p as I,q as P,r as A,s as B,t as M,u as E,v as D,w as N,x as R,y as V,z as q,A as O,B as F,C as L,D as X,E as U,F as W,G as j,H as G,I as $,J as H,K,L as Y}from"./safetybyassem.cac66048.js";import{_ as z}from"./zap.f36d0134.js";import{_ as J,a as Q}from"./solderingBrrc2205ondeckTOescDYSzap.88e9149b.js";import{_ as Z}from"./startPDBtest.e8cc6a5b.js";import{_ as tt}from"./zapPDBtest.fd538837.js";var et="/clover-vuepress/assets/cutwire14AWG.97a2ed95.jpg",it="/clover-vuepress/assets/mount5vconnector.31bca9f9.jpg",ot="/clover-vuepress/assets/casebattery.987dce51.png",rt="/clover-vuepress/assets/safetyPower.05207682.png",at="/clover-vuepress/assets/connectionPixhawk.a5b3400e.png",nt="/clover-vuepress/assets/isoViewmountHolder.a7829205.png";const lt={},st=a('<h1 id="clover-2-construction-kit-assembly-instruction" tabindex="-1"><a class="header-anchor" href="#clover-2-construction-kit-assembly-instruction" aria-hidden="true">#</a> Clover 2 construction kit assembly instruction</h1><p><img src="'+p+'" alt="Clover"></p><h2 id="the-constructor-kit-contents" tabindex="-1"><a class="header-anchor" href="#the-constructor-kit-contents" aria-hidden="true">#</a> The constructor kit contents</h2><p><img src="'+m+'" alt="Explosion"></p><ul><li>Central frame \xD72.</li><li>Additional frame \xD74.</li><li>Motor mount \xD78.</li><li>Legs x8.</li><li>Motor mount guard \xD78.</li><li>Propeller guard \xD716.</li><li>Side guard \xD716.</li><li>Dalprop 5045 plastic propeller \xD74.</li><li>Racerstar BR2205 2300kV brushless motor \xD74.</li><li>Speed controllers ESC, DYS XSD20A \xD74.</li><li>Power controller XT60 pin \xD71.</li><li>Power connector XT60 socket \xD71.</li><li>Three-wire female-female flat cable \xD72.</li><li>Multicore silicone insulated copper wire 14AWG (red, black), 50 cm long</li><li>Power distribution board PDB BeeRotor Power Distribution Board V2.0 \xD71.</li><li>Li-ion rechargeable battery (battery) 18650 \xD78.</li><li>EFEST Luc V4 Li-lon Charger \xD71.</li><li>Protective case for regulators \xD74.</li><li>Legs attachment \xD78.</li><li>Pixhawk flight controller \xD71.</li><li>FlySky i6 radio receiver \xD71.</li><li>FlySky i6 radio transmitter \xD71.</li><li>EFEST LUC V4 Charger \xD71.</li><li>Micro USB to USB Cable \xD71</li><li>Battery compartment 18650 Li-ion \xD71</li><li>Wire copper multicore silicone insulated cable 18AWG (red, black), 100 cm long</li><li>AA battery \xD74</li><li>Jumper, Bind-plug</li></ul><h3 id="fasteners" tabindex="-1"><a class="header-anchor" href="#fasteners" aria-hidden="true">#</a> Fasteners</h3><ul><li>6 mm plastic legs \xD728.</li><li>30 mm plastic legs \xD732.</li><li>M3x8 screws \xD748.</li><li>M3x12 screws \xD724.</li><li>M3x16 screws \xD740.</li><li>Plastic nuts \xD78.</li><li>Metal nuts \xD748.</li><li>Stickers for the battery compartment \xD78.</li><li>Thermal contraction tube \u230015, .50 cm</li><li>Thermal contraction tube \u23005, 100 cm</li><li>Double-sided 3M adhesive tape \xD716.</li><li>Screwdriver \xD71 (visualization needed)</li><li>Insulation tape \xD71</li><li>Scissors \xD71</li><li>Strap for the battery 250 mm \xD71</li></ul><h2 id="flysky-i6-transmitter" tabindex="-1"><a class="header-anchor" href="#flysky-i6-transmitter" aria-hidden="true">#</a> Flysky i6 transmitter</h2><ol><li>Switch A (SwA).</li><li>Switch B (SwB).</li><li>Switch (SwC).</li><li>Switch D (SwD).</li><li>Left stick.</li><li>Right stick.</li><li>Left trimmer.</li><li>Right trimmer.</li><li>Up button.</li><li>Down button.</li><li>OK button.</li><li>Cancel button.</li><li>BIND KEY button.</li><li>POWER switch.</li><li>LCD.</li><li>Handle A (VrA).</li><li>Handle B (VrB).</li></ol><p><img src="'+u+'" alt="radio Transmitter"></p><h2 id="additional-equipment" tabindex="-1"><a class="header-anchor" href="#additional-equipment" aria-hidden="true">#</a> Additional equipment</h2><h3 id="this-equipment-is-not-part-of-the-clover-2-constructor-kit-but-it-is-required-for-the-assembly-process" tabindex="-1"><a class="header-anchor" href="#this-equipment-is-not-part-of-the-clover-2-constructor-kit-but-it-is-required-for-the-assembly-process" aria-hidden="true">#</a> This equipment is not part of the Clover 2 constructor kit, but it is required for the assembly process</h3><ol><li>Soldering iron</li><li>Colophony/ Flux (neutral)</li><li>Solder</li><li>Hot air gun</li><li>Pliers</li><li>Pincers</li><li>Stationery knife</li><li>Multimeter</li></ol><p><img src="'+f+'" alt="Additional equipment"></p>',14),ht=e("Soldering safety"),ct=a('<h2 id="assembly-order" tabindex="-1"><a class="header-anchor" href="#assembly-order" aria-hidden="true">#</a> Assembly order</h2><h3 id="installation-of-motors" tabindex="-1"><a class="header-anchor" href="#installation-of-motors" aria-hidden="true">#</a> Installation of motors</h3><ul><li>Unpack the motors. Using pliers, shorten the wires on the motors by cutting half their length (leaving about 25 mm).</li></ul><p><img src="'+g+'" alt="brrc2205 motor"></p><p>Strip</p><ul><li>remove 2 mm of insulation from the ends of the wire without damaging the copper strands.</li></ul><p>Twist the wires.</p><p>Tin wires</p><ul><li>Apply flux to the exposed part of the wire.</li><li>Cover the solder using tweezers.</li></ul><p><img src="'+z+'" alt="Tinning"></p><h4 id="fix-the-motor-on-the-mount" tabindex="-1"><a class="header-anchor" href="#fix-the-motor-on-the-mount" aria-hidden="true">#</a> Fix the motor on the mount</h4><ul><li>Install the motor on the engraved side of the mount.</li><li>Attach the motors to the mounts with M3x8 screws using a screwdriver.</li></ul><p><img src="'+b+'" alt="Fix the motor on the mount"></p><ul><li>Mounts with motors should be arranged according to the diagram. The arrows indicate the direction of motor rotation direction.</li></ul><p><img src="'+w+'" alt="Rotation of motors"></p><h3 id="tin-three-contact-pads-of-the-speed-controller" tabindex="-1"><a class="header-anchor" href="#tin-three-contact-pads-of-the-speed-controller" aria-hidden="true">#</a> Tin three contact pads of the speed controller</h3><ul><li>Apply flux</li><li>Apply solder</li></ul><p>By warming up the contact pads of the controller, the tin will evenly fill the entire pad. To do so, apply heat by holding the soldering iron on the contact pads for 2 sencods (or more if needed).</p><p><img src="'+J+'" alt="Tinning of the controller contact pads"></p><ul><li>Repeat this operation for the remaining three ESC</li></ul><h3 id="solder-the-wires-of-the-motors-to-the-esc" tabindex="-1"><a class="header-anchor" href="#solder-the-wires-of-the-motors-to-the-esc" aria-hidden="true">#</a> Solder the wires of the motors to the ESC</h3><p>Solder the prepared wires of the motors to the pads of the controllers.</p><p><img src="'+Q+'" alt="Solder wires of motors to the controllers"></p><ul><li>Repeat this operation for the remaining three ESC</li></ul><h3 id="power-connectors-installation" tabindex="-1"><a class="header-anchor" href="#power-connectors-installation" aria-hidden="true">#</a> Power connectors installation</h3><h4 id="preparing-wires-for-xt60-power-connectors" tabindex="-1"><a class="header-anchor" href="#preparing-wires-for-xt60-power-connectors" aria-hidden="true">#</a> Preparing wires for XT60 power connectors</h4><ol><li>Take a bundle of red and black wires marked 14AWG</li><li>Cut 4 pieces of wire of the following lengths</li></ol><ul><li>Length 7 cm (XT60 pin power connector) - 1 red, 1 black</li><li>Length 9 cm (XT60 socket power connector) - 1 red, 1 black</li></ul><p><img src="'+et+'" alt="Preparing wires for the power connector"></p><h4 id="preparing-xt60-pin-and-xt60-socket-high-power-connectors" tabindex="-1"><a class="header-anchor" href="#preparing-xt60-pin-and-xt60-socket-high-power-connectors" aria-hidden="true">#</a> Preparing XT60 pin and XT60 socket high-power connectors</h4>',30),dt=e("Article about high-power connectors and their designations"),pt=a('<p><img src="'+_+'" alt="XT60 high-power connector"></p><ol><li>Tin two red and black 14AWG 7 cm long power wires for the XT60 pin connector.</li><li>Tin contact pads of the XT60 pin connector.</li><li>Solder the black wire to the \u201C-\u201D contact of the connector.</li><li>Solder the red wire to the \u201C+\u201D contact of the connector.</li><li>Cut \u23005 heat-shrink tubing (2 sections \xD7 10 mm).</li><li>Slip the \u23005 heat-shrink tubing tube on the wires so that they cover the contact pads of the wires from XT60.</li><li>Shrink the heat-shrink tubing with a hot air gun. <img src="'+y+'" alt="Installation of XT60 connectors"></li><li>Repeat the procedure for XT60 socket connector.</li></ol><h4 id="preparation-of-the-5v-power-connectors-for-the-control-circuit" tabindex="-1"><a class="header-anchor" href="#preparation-of-the-5v-power-connectors-for-the-control-circuit" aria-hidden="true">#</a> Preparation of the 5V power connectors for the control circuit</h4><ol><li>Trim/pull out all pins from one of the connectors. Disconnect it.</li><li>Using an utility knife, pry the retainer off on the remaining connector to release the 3rd wire.</li><li>Remove the 3rd (orange) wire from the connector, since it is not needed.</li><li>The length of the remaining black and red wires should be of 10 \u2013 12 cm.</li></ol><p><img src="'+it+'" alt="Installation of the 5V connector"></p><h3 id="installation-of-the-power-distribution-board" tabindex="-1"><a class="header-anchor" href="#installation-of-the-power-distribution-board" aria-hidden="true">#</a> Installation of the power distribution board</h3><h4 id="pre-soldering-check" tabindex="-1"><a class="header-anchor" href="#pre-soldering-check" aria-hidden="true">#</a> Pre-soldering check</h4>',7),mt=e("Article about continuity test"),ut=a('<p><img src="'+Z+'" alt=" Pre-soldering check"></p><p>Check OPEN CONDITION of the following circuits (the multimeter does not beep):</p><ul><li>\u201CBAT+\u201D and \u201CBAT-\u201D</li><li>\u201C12V\u201D and \u201CGND\u201D</li><li>\u201C5V\u201D and \u201CGND\u201D</li></ul><p>Check CLOSED CONDITION of the following circuits (the multimeter beeps):</p><ul><li>\u201CBAT-\u201D with every contact marked \u201C-\u201D and \u201CGND\u201D</li><li>\u201CBAT+\u201D, with every contact marked \u201C+\u201D</li></ul><h3 id="tin-the-contact-pads-of-the-power-board" tabindex="-1"><a class="header-anchor" href="#tin-the-contact-pads-of-the-power-board" aria-hidden="true">#</a> Tin the contact pads of the power board</h3>',6),ft=e("Tin*"),gt=e(" the contact pads of the power board."),bt=t("li",null,"Using a multimeter, check absence of short-circuits on the PCB (check continuity).",-1),wt=a('<p><img src="'+tt+'" alt=" After-soldering check"></p><p>By warming up the contact pads of the controller, the tin will evenly fill the entire pad. To do so, apply heat by holding the soldering iron on the contact pads for 2 sencods (or more if needed).</p><h4 id="soldering-the-xt60-high-power-connector" tabindex="-1"><a class="header-anchor" href="#soldering-the-xt60-high-power-connector" aria-hidden="true">#</a> Soldering the XT60 high power connector</h4><p>Solder the connector for battery, taking into account the polarity on the contact pads.</p><p><img src="'+n+'" alt="Soldering of XT60 to PDB"></p><p>IMPORTANT NOTE about polarity</p><ul><li>the red wire is \u201C+\u201D</li><li>the black wire is \u201C-\u201D</li></ul><h4 id="soldering-of-the-power-connector-for-the-5v-control-circuit" tabindex="-1"><a class="header-anchor" href="#soldering-of-the-power-connector-for-the-5v-control-circuit" aria-hidden="true">#</a> Soldering of the power connector for the 5V control circuit</h4><p>Solder the 5V connector, taking into account the polarity on the contact pads. (in the picture: the red wire is \u201C+\u201D)</p><p><img src="'+n+'" alt="Soldering of 5V to PDB"></p><h3 id="installation-of-the-battery-compartment" tabindex="-1"><a class="header-anchor" href="#installation-of-the-battery-compartment" aria-hidden="true">#</a> Installation of the battery compartment</h3><h4 id="preparation-of-jumpers-3-pcs" tabindex="-1"><a class="header-anchor" href="#preparation-of-jumpers-3-pcs" aria-hidden="true">#</a> Preparation of jumpers (3 pcs.)</h4><p><img src="'+k+'" alt="Jumper"></p><ul><li>Cut off 2 cm of high-power wire.</li><li>Strip on both ends.</li><li>Tin.</li><li>Make 3 jumpers.</li><li>Solder the jumpers according to the diagram.</li><li>Check for continuity with a multimeter. If necessary, clean with sand paper.</li></ul><h4 id="preparation-of-the-battery-compartment" tabindex="-1"><a class="header-anchor" href="#preparation-of-the-battery-compartment" aria-hidden="true">#</a> Preparation of the battery compartment</h4><p><img src="'+ot+'" alt="Preparation of the battery compartment"></p><ul><li>Conforming to the polarity, glue the sticker with markings inside the battery compartment.</li><li>Stick a strip of adhesive tape to the bottom of the compartment.</li></ul><h3 id="installation-of-the-power-distribution-board-1" tabindex="-1"><a class="header-anchor" href="#installation-of-the-power-distribution-board-1" aria-hidden="true">#</a> Installation of the power distribution board</h3><ul><li>Fix the power board to the frame with M3x8 screws and plastic nuts. <img src="'+x+'" alt="Installation of the PDB board"><blockquote><p><strong>IMPORTANT</strong> The white arrow on the BeeRotor board points towards the fore cutout. <img src="'+v+'" alt="Installation of the PDB board"></p></blockquote></li></ul><h4 id="installation-of-elements" tabindex="-1"><a class="header-anchor" href="#installation-of-elements" aria-hidden="true">#</a> Installation of elements</h4><ol><li>Install the nuts into plastic holders. <img src="'+T+'" alt="Installation of plastic holders"></li><li>Fix the motor mounts to the frame with M3x16 screws. <ul><li>The mounts are installed above the frame.</li><li>Plastic holders are installed beneath the frame. <img src="'+S+'" alt="Installation of mounts"></li></ul></li><li>Arrangement of motors. Check arrangement of the motors (the motors with black nuts should be in the top left and lower right corners). <img src="'+C+'" alt="Arrangement of motors"></li><li>Put the power wires of the ESC through the holes. <img src="'+I+'" alt="high-power wires of motors"></li></ol><h4 id="soldering-the-high-power-circuit-board" tabindex="-1"><a class="header-anchor" href="#soldering-the-high-power-circuit-board" aria-hidden="true">#</a> Soldering the high-power circuit board</h4><p>Solder the high-power wires of the ESC to the power supply board observing polarity.</p><p><img src="'+P+'" alt="Soldering of high-power wires to PDB"></p><p>IMPORTANT NOTE about polarity</p><ul><li>the red wire is \u201C+\u201D</li><li>the black wire is \u201C-\u201D</li></ul><h3 id="pairing-the-receiver-and-transmitter" tabindex="-1"><a class="header-anchor" href="#pairing-the-receiver-and-transmitter" aria-hidden="true">#</a> Pairing the receiver and transmitter</h3><ol><li>Connect the radio receiver to the 5V connector. In any connector the GND is in the bottom. In the diagram, the power is labeled 5V <img src="'+A+'" alt="Connecting the receiver power"></li><li>Connect the battery. The LED on the radio receiver should be flashing. ![Connecting the battery]</li></ol><h4 id="safety-when-working-with-the-battery" tabindex="-1"><a class="header-anchor" href="#safety-when-working-with-the-battery" aria-hidden="true">#</a> SAFETY when working with the battery</h4><p><img src="'+rt+'" alt="SAFETY when working with the battery"></p><h4 id="enabling-the-transmitter" tabindex="-1"><a class="header-anchor" href="#enabling-the-transmitter" aria-hidden="true">#</a> Enabling the transmitter</h4><ol><li>Insert the jumper into B/VCC of the radio receiver (short &quot;ground&quot; and &quot;signal&quot;)</li><li>On the transmitter, hold down the BIND KEY button.</li><li>Power up the transmitter (flip the POWER switch, do not release BIND KEY).</li><li>Connect the battery to the drone.</li><li>Wait for synchronization.</li><li>Disconnect the jumper.</li><li>The LED will remain ON continuously.</li></ol><p><img src="'+B+'" alt="Connecting the radio receiver power"></p>',33),_t=e("Radio equipment troubleshooting manual"),yt=a('<h3 id="checking-the-motors-direction-of-rotation" tabindex="-1"><a class="header-anchor" href="#checking-the-motors-direction-of-rotation" aria-hidden="true">#</a> Checking the motors direction of rotation</h3><ol><li>Apply stickers to the 18650 batteries.</li><li>Install the 18650 batteries into the compartment observing polarity. <img src="'+M+'" alt="Battery compartment readiness"></li><li>Check that the 5V power plug is connected to the receiver according to the circuit diagram.</li><li>Connect the motor ESC to channel 3, marked as CH3 on the receiver as on the circuit diagram. <img src="'+E+'" alt="Connecting the ESC to the receiver"></li><li>Connect external power (battery).</li><li>Turn the transmitter ON.</li><li>Using the left stick, set throttle to 10 %.</li><li>Check the motor direction of rotation according to the scheme. <img src="'+D+'" alt="Checking motors rotation"></li><li>If you have to change the rotation direction, swap any two phase wires of the motor (needs resoldering). <img src="'+N+'" alt="Resoldering phase wires"></li></ol><h3 id="installation-of-the-radio-receiver" tabindex="-1"><a class="header-anchor" href="#installation-of-the-radio-receiver" aria-hidden="true">#</a> Installation of the radio receiver</h3><ol><li>Install the 30 mm plastic legs on the frame with M3x8 screws.</li><li>Pass the 5V power connector through the slit. <img src="'+R+'" alt="Installation of legs and the slot"></li><li>Attach the receiver to the bottom of the additional frame using double-sided adhesive tape and following the orientation of the engraved arrow. The antennas are to be pointing forward. <img src="'+V+'" alt="Installation of the radio receiver on the deck"></li><li>Install the 3-wire flat cable into the PPM / CH1 channel. <img src="'+q+'" alt="Connecting the radio receiver"></li><li>Pass them through the slit to the 5 V connector.</li><li>Screw the bottom an additional frame to the legs on the central frame with M3x8 screws. <img src="'+O+'" alt="Bottom deck installation"><blockquote><p><strong>IMPORTANT</strong> The directions of the arrows on the power supply board and the additional frame should coincide</p></blockquote></li></ol><h3 id="installation-of-the-flight-controller" tabindex="-1"><a class="header-anchor" href="#installation-of-the-flight-controller" aria-hidden="true">#</a> Installation of the flight controller</h3><h4 id="turn-the-assembly-upside-down" tabindex="-1"><a class="header-anchor" href="#turn-the-assembly-upside-down" aria-hidden="true">#</a> Turn the assembly upside down</h4><p><img src="'+F+'" alt="Turn the assembly upside down"></p><h4 id="installation-of-the-pixhawk-flight-controller" tabindex="-1"><a class="header-anchor" href="#installation-of-the-pixhawk-flight-controller" aria-hidden="true">#</a> Installation of the Pixhawk flight controller</h4><ol><li>Stick the two-sided adhesive tape in the corners of the flight controller. <img src="'+L+'" alt="Flight controller"><blockquote><p><strong>IMPORTANT</strong> When the motors rotate, vibrations occur, which affect sensors of the Pixhawk flight controller. To avoid this effect, the number of double-sided tape layers should be increased up to 4 \u2013 5.</p></blockquote></li><li>Install the flight controller in the center of the frame. <img src="'+X+'" alt="Flight controller"><blockquote><p><strong>IMPORTANT</strong> The arrows on the frame and Pixhawk should point in the same direction</p></blockquote></li></ol><h4 id="connecting-the-flight-controller-according-to-the-circuit-diagram" tabindex="-1"><a class="header-anchor" href="#connecting-the-flight-controller-according-to-the-circuit-diagram" aria-hidden="true">#</a> Connecting the flight controller according to the circuit diagram</h4><ol><li>Connect PPM (three-wire flat cable) to the RCIN port</li><li>Motors to MAIN OUT ports 1,2,3,4, according to the circuit diagram</li><li>Power by PDB (5V/VCC) to any port except for SB (SBUS)</li></ol><p><img src="'+at+'" alt="Connecting the flight controller"></p><h3 id="esc-assembly" tabindex="-1"><a class="header-anchor" href="#esc-assembly" aria-hidden="true">#</a> ESC assembly</h3><ol><li>Stick the double-sided adhesive tape to the base of the ESC protective case.</li><li>Put the ESCs into protective cases. Fasten the assembly to the motor mounts of the frame. <img src="'+U+'" alt="ESC cases top view"></li></ol><h3 id="installation-of-guard" tabindex="-1"><a class="header-anchor" href="#installation-of-guard" aria-hidden="true">#</a> Installation of guard</h3><ol><li>Attach the lower guard with M3x16 screw to the motor mounts of the frame. <img src="'+W+'" alt="Installation of beams guard"></li><li>Attach the feet to the plastic holders with M3x16 screws. <img src="'+j+'" alt="Feet installation"></li><li>Attach the 30 mm long legs to the holes of the lower guard with M3x12 screw. <img src="'+G+'" alt="Installation of the lower radial guard"></li><li>Attach the top guard with M3x12 screws. <img src="'+$+'" alt="Installation of the top radial guard"></li></ol><h3 id="installation-of-the-battery-compartment-1" tabindex="-1"><a class="header-anchor" href="#installation-of-the-battery-compartment-1" aria-hidden="true">#</a> Installation of the battery compartment</h3><p>Requires the following components:</p><ul><li>M3x12 screws (4 pcs)</li><li>M3 nuts (4 pcs)</li><li>Additional frame (1 pc)</li><li>Battery compartment (1 pc)</li></ul><ol><li>Attach the battery compartment on top of the additional frame with M3x12 screws and nuts. <img src="'+H+'" alt="Installation of the battery compartment"></li><li>Attach the top additional frame to the legs with M3x8 screws. <img src="'+nt+'" alt="Installation of the battery compartment"></li><li>Install the battery into the battery compartment.</li></ol><h3 id="installation-of-antennas" tabindex="-1"><a class="header-anchor" href="#installation-of-antennas" aria-hidden="true">#</a> Installation of antennas</h3><ol><li>Attach antennas on double-sided adhesive tape or duct tape, and put the antennae into the front holes of the top additional frame.</li></ol><p><img src="'+K+'" alt="Installation of the battery compartment"></p><p>The drone is ready for configuration!</p><h2 id="safety-notes-for-assembly-and-configuration" tabindex="-1"><a class="header-anchor" href="#safety-notes-for-assembly-and-configuration" aria-hidden="true">#</a> Safety notes for assembly and configuration</h2><ol><li>Remove the propellers.\u201CAll ground operations are to be performed with propellers removed. Propellers are to be installed on the motors before the flight only.\u201D</li><li>Disconnect the battery. Keep the power off. \u201CAssembly, configuration, and maintenance should be performed with power disconnected. Connect power only for testing electronic components of the drone. After testing, power is to be disconnected before other works.\u201D</li><li>Call for help. \u201CIf you experience problem when working with the drone, contact the instructor or the teacher, do not try to solve the problem yourself.\u201D</li></ol><p><img src="'+Y+'" alt="Safety during assembly"></p><h2 id="security-when-working-with-18650-li-ion-batteries" tabindex="-1"><a class="header-anchor" href="#security-when-working-with-18650-li-ion-batteries" aria-hidden="true">#</a> Security when working with 18650 Li-ion batteries</h2><ol><li>Handle batteries carefully. Avoid falls, bumps, and deformations.</li><li>When connecting (disconnecting) batteries, hold only the connectors, never pull or tug the wires.</li><li>If you see open connectors, violation of insulation or battery compartment integrity, do not touch it, and immediately inform the instructor or teacher.</li></ol>',29),kt=e("See article "),xt=e("safety precautions when soldering and during drone flight operation");function vt(Tt,St){const i=s("RouterLink");return h(),c(d,null,[st,t("p",null,[o(i,{to:"/en/tb.html"},{default:r(()=>[ht]),_:1})]),ct,t("p",null,[o(i,{to:"/en/connectortypes.html"},{default:r(()=>[dt]),_:1})]),pt,t("p",null,[o(i,{to:"/en/test_connection.html"},{default:r(()=>[mt]),_:1})]),ut,t("ol",null,[t("li",null,[o(i,{to:"/en/tinning.html"},{default:r(()=>[ft]),_:1}),gt]),bt]),wt,t("p",null,[o(i,{to:"/en/radioerrors.html"},{default:r(()=>[_t]),_:1})]),yt,t("p",null,[kt,o(i,{to:"/en/safety.html"},{default:r(()=>[xt]),_:1})])],64)}var Et=l(lt,[["render",vt],["__file","assemble_2.html.vue"]]);export{Et as default};
