import{_ as n,r as s,o as l,c,a as t,b as r,w as d,F as h,d as e,e as m}from"./app.4f6a4bec.js";import{_ as p,a as g,b as _,c as b,d as w,e as u,f,g as z,h as x,i as v,j as k,k as y,l as I,m as M}from"./hts-6.d8183b9c.js";import{_ as S,a as C,b as P,c as D,d as E,e as R,f as T}from"./frame_7.7e486a8f.js";import{_ as N}from"./props_rotation.3b4364c5.js";import{_ as B,a as L,b as A,c as q,d as V,e as G,f as o,g as W,h as F,i as J,j as O,k as U,l as X,m as j,n as H,o as Y,p as K,q as Q,r as Z,s as $,t as tt,u as et,v as st,w as rt,x as ot,y as it,z as at,A as nt,B as lt,C as ct,D as dt,E as ht,F as mt,G as pt,H as gt,I as _t,J as bt,K as wt,L as ut,M as ft,N as zt,O as xt,P as vt,Q as kt,R as yt,S as It,T as Mt,U as St,V as Ct,W as Pt,X as Dt,Y as Et,Z as Rt}from"./guard_6.1e374a4c.js";import{_ as Tt,a as Nt}from"./led_8.a6b00278.js";const Bt={},Lt=t("h1",{id:"clover-4-assembly",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#clover-4-assembly","aria-hidden":"true"},"#"),e(" Clover 4 assembly")],-1),At=e("Dimensional drawing \u2013\xA0"),qt={href:"https://github.com/CopterExpress/clover/raw/master/docs/assets/dimensional-drawing/clover-4.2-ws.pdf",target:"_blank",rel:"noopener noreferrer"},Vt=e("clover-4.2-ws.pdf"),Gt=e("."),Wt=m('<h2 id="fasteners-size" tabindex="-1"><a class="header-anchor" href="#fasteners-size" aria-hidden="true">#</a> Fasteners size</h2><div class="custom-container tip"><p class="custom-container-title">TIP</p><p>During assembly, screws and racks of various sizes are used, using fasteners of the wrong size can damage the copter.</p></div><table class="type_table"><tr><td><img src="'+p+'" width="70" class="center"></td><td>Screw M3x10</td><td><img src="'+g+'" width="100" class="center"></td><td>Aluminium rack 40mm</td></tr><tr><td><img src="'+_+'" width="60" class="center"></td><td>Screw M3x8</td><td><img src="'+b+'" width="70" class="center"></td><td>Aluminium rack 15mm</td></tr><tr><td><img src="'+w+'" width="40" class="center"></td><td>Screw M3x5</td><td><img src="'+u+'" width="100" class="center"></td><td>Nylon rack 40mm</td></tr><tr><td><img src="'+f+'" width="40" class="center"></td><td>Screw M2x5</td><td><img src="'+z+'" width="80" class="center"></td><td>Nylon rack 30mm</td></tr><tr><td><img src="'+x+'" width="25" class="center"></td><td> Nut M3 (self-locking)</td><td><img src="'+v+'" width="70" class="center"></td><td>Nylon rack 20mm</td></tr><tr><td><img src="'+k+'" width="25" class="center"></td><td> Nut M3 (nylon)</td><td><img src="'+y+'" width="60" class="center"></td><td>Nylon rack 15mm</td></tr><tr><td><img src="'+I+'" width="50" class="center"></td><td>Damper rack</td><td><img src="'+M+'" width="50" class="center"></td><td> Nylon rack 6mm</td></tr></table><h2 id="frame-assembly" tabindex="-1"><a class="header-anchor" href="#frame-assembly" aria-hidden="true">#</a> Frame Assembly</h2><ol><li><p>Align the 4 beams with the center deck, fix them with the M3x8 screws and self-locking nuts.</p><div class="image-group"><img src="'+S+'" width="300" class="zoom border"><img src="'+C+'" width="300" class="zoom border"></div></li><li><p>Install 2 15mm posts on the center holes in the main deck and fix them with the M3x8 screws.</p><img src="'+P+'" width="300" class="zoom border center"></li><li><p>Install the stiffener hook into the groove in the beam.</p><img src="'+D+'" width="300" class="zoom border center"></li><li><p>Hold the stiffeners tight to the main deck.</p><img src="'+E+'" width="300" class="zoom border center"></li><li><p>Tighten the stiffeners with a small carbon deck.</p><img src="'+R+'" width="300" class="zoom border center"></li><li><p>Install 4 6mm nylon posts and fix them with the M3x5 screws.</p><img src="'+T+'" width="300" class="zoom border center"></li></ol><h2 id="installing-of-motors" tabindex="-1"><a class="header-anchor" href="#installing-of-motors" aria-hidden="true">#</a> Installing of motors</h2><ol><li><p>When installing motors, pay attention to the rotation scheme of the motors. The rotation mark on the motors must match the rotation scheme.</p><img id="prop_rotation" src="'+N+'" width="300" class="zoom border center"></li><li><p>Mount the motor on the corresponding holes in the beam using <strong>M3x5 screws</strong>.</p><div class="image-group"><img src="'+B+'" width="300" class="zoom border"><img src="'+L+'" width="300" class="zoom border"></div></li></ol><div class="custom-container warning"><p class="custom-container-title">WARNING</p><p>Make sure that the motors are secured with M3x5 screws, otherwise a short circuit between the windings may occur.</p></div><h2 id="installing-esc-and-pdb" tabindex="-1"><a class="header-anchor" href="#installing-esc-and-pdb" aria-hidden="true">#</a> Installing ESC and PDB</h2><ol><li><p>Install the Power Distribution Board (PDB) on the pre-mounted stands, it must be installed with the power cable pointing towards the rear of the aircraft.</p><div class="image-group"><img src="'+A+'" width="300" class="zoom border"><img src="'+q+'" width="300" class="zoom border"></div></li><li><p>Install the speed controllers (ESC) to the appropriate positions on the beam.</p><div class="image-group"><img src="'+V+'" width="300" class="zoom border"><img src="'+G+'" width="300" class="zoom border"></div></li><li><p>Tighten the speed controllers (ESC) with cable ties.</p><img id="prop_rotation" src="'+o+'" width="300" class="zoom border center"></li><li><p>Measure out the required amount of ESC power wire, and cut off the excess.</p></li><li><p>Strip and tin the cut wires</p></li><li><p>Tin the contact pads on the power distribution board.</p></li><li><p>Solder the ESC power wires to the power distribution board.</p><div class="image-group"><img src="'+o+'" width="300" class="zoom border"><img src="'+W+'" width="300" class="zoom border"></div><blockquote><p><strong>Caution</strong> Be careful with the pin signatures on the board. The red wire should go to the site with the signature <em>+</em>, and the black one to the signature <em>-</em>.</p></blockquote></li><li><p>Cut off the excess phase cable coming from the motors.</p></li><li><p>Strip and tin the phase cables.</p></li><li><p>Tin the contact pads of the governors.</p></li><li><p>Solder the phase cables to the contact pads of the regulators in any order.</p><div class="image-group"><img src="'+F+'" width="300" class="zoom border"><img src="'+J+'" width="300" class="zoom border"></div></li><li><p>Solder 3 female JST connectors to <em>5V</em> pads and <em>bat+</em> pad</p><div class="image-group"><img src="'+O+'" width="300" class="zoom border"><img src="'+U+'" width="300" class="zoom border"></div></li></ol><h2 id="installing-the-flight-controller" tabindex="-1"><a class="header-anchor" href="#installing-the-flight-controller" aria-hidden="true">#</a> Installing the flight controller</h2><p>The <em>Clover 4</em> set allows you to install various flight controllers, for example <em>COEX Pix</em> and <em>Pixracer</em>.</p><div class="custom-container warning"><p class="custom-container-title">WARNING</p><p>When installing the flight controller, pay attention to the arrow located on the board, when installing it must be directed towards the nose of the aircraft.</p></div><h3 id="coex-pix" tabindex="-1"><a class="header-anchor" href="#coex-pix" aria-hidden="true">#</a> COEX Pix</h3><div class="custom-container tip"><p class="custom-container-title">TIP</p><p>Before installing the damper struts, screw in 2 layers of nylon nuts for a stronger hold or bite off excess threads with side cutters.</p></div><ol><li><p>Secure the power distribution board with nylon nuts and mount the damper posts on top.</p></li><li><p>Install the flight controller and secure with nylon nuts.</p><img id="prop_rotation" src="'+X+'" width="300" class="zoom border center"></li><li><p>Insert the flash card for logging into the flight controller.</p><img id="prop_rotation" src="'+j+'" width="300" class="zoom border center"></li></ol><h3 id="pixracer" tabindex="-1"><a class="header-anchor" href="#pixracer" aria-hidden="true">#</a> Pixracer</h3><ol><li><p>Secure the power distribution board with 6mm nylon struts.</p></li><li><p>Install the small mounting deck and secure with nylon nuts.</p><img id="prop_rotation" src="'+H+'" width="300" class="zoom border center"></li><li><p>Glue 3-4 layers of double-sided tape, glue it in the center of the small deck and place the <em>Pixracer</em> on top.</p><img id="prop_rotation" src="'+Y+'" width="300" class="zoom border center"></li><li><p>Insert the MicroSD card into the flight controller.</p><img id="prop_rotation" src="'+K+'" width="300" class="zoom border center"></li></ol><h2 id="connecting-a-flight-controller" tabindex="-1"><a class="header-anchor" href="#connecting-a-flight-controller" aria-hidden="true">#</a> Connecting a flight controller</h2><ol><li><p>Connect the ESCs to the flight controller according to the scheme.</p><img src="'+Q+'" width="300" class="zoom border center"></li><li><p>Connect the power cable to the power distribution board (PDB) and the corresponding connector on the flight controller.</p><div class="image-group"><img src="'+Z+'" width="300" class="zoom border"><img src="'+$+'" width="300" class="zoom border"></div></li><li><p>Install the 40mm aluminum posts onto the M3x10 screws.</p><div class="image-group"><img src="'+tt+'" width="300" class="zoom border"><img src="'+et+'" width="300" class="zoom border"></div></li></ol><h2 id="installing-raspberry-pi" tabindex="-1"><a class="header-anchor" href="#installing-raspberry-pi" aria-hidden="true">#</a> Installing Raspberry Pi</h2><ol><li><p>Place the 20 mm and 40 mm posts on the mounting deck, fix them with M3x8 screws.</p><img src="'+st+'" width="300" class="zoom border center"></li><li><p>Use an M3x10 bolt to cut M3 carving in the Raspberry Pi mounting holes.</p></li><li><p>Screw the 6mm racks into the Raspberry Pi board, secure them with nylon nuts if necessary.</p><div class="image-group"><img src="'+rt+'" width="300" class="zoom border"><img src="'+ot+'" width="300" class="zoom border"></div></li><li><p>Mount the Raspberry Pi onto the mounting deck using M3x6 screws.</p><img src="'+it+'" width="300" class="zoom border center"></li><li><p>Install the assembled module into the corresponding slots on the main deck of the drone.</p><img src="'+at+'" width="300" class="zoom border center"></li><li><p>Plug the 5V JST into the corresponding power pins on the Raspberry Pi.</p><img src="'+nt+'" width="300" class="zoom border center"></li><li><p>Take 4 Dupont wires, cut 5\u20137 cm of cable and solder to the corresponding pins of the rangefinder.</p><div class="image-group"><img src="'+lt+'" width="300" class="zoom border"><img src="'+ct+'" width="300" class="zoom border"></div><table><thead><tr><th>Wire</th><th>Rangefinder pin</th></tr></thead><tbody><tr><td><font color="red">Red</font></td><td>5v</td></tr><tr><td><font color="black">Black</font></td><td>GND</td></tr><tr><td><font color="yellow">Yellow</font></td><td>SDA</td></tr><tr><td><font color="green">Green</font></td><td>SCL</td></tr></tbody></table><div class="image-group"><img src="'+dt+'" width="300" class="zoom border"><img src="'+ht+'" width="300" class="zoom border"></div></li><li><p>Install the rangefinder on the grip deck and glue the radio to the 3M tape.</p><blockquote><p><strong>Caution</strong> Install the rangefinder so that the nuts do not rest directly on the board. With this installation, if there is a high probability of damaging the board elements.</p></blockquote><img src="'+mt+'" width="300" class="zoom border center"></li><li><p>Install 4 20mm nylon posts and fix them with M3x8 bolts.</p><img src="'+pt+'" width="300" class="zoom border center"></li><li><p>Place the camera on the small mounting deck and secure it with two short self-tapping screws.</p><blockquote><p><strong>Caution</strong> If you attach the camera to the upper right corner and the screw head touches the element on the camera, the camera will not work.</p></blockquote><img src="'+gt+'" width="300" class="zoom border center"></li><li><p>Place the small mounting deck with the camera on the stands and secure with the M3x8 bolts.</p><img src="'+_t+'" width="300" class="zoom border center"></li><li><p>Place the assembled module over the Raspberry Pi module and fix it with M3x8 bolts.</p><img src="'+bt+'" width="300" class="zoom border center"></li><li><p>Connect the camera and Raspberry Pi with a ribbon cable.</p><img src="'+wt+'" width="300" class="zoom border center"></li><li><p>Connect the rangefinder to the Raspberry Pi into the appropriate pins.</p><img src="'+ut+'" width="300" class="zoom border center"></li><li><p>Connect the radio and the flight controller with a 4-pin cable.</p><img src="'+ft+'" width="300" class="zoom border center"></li></ol><h2 id="installing-led-strip-and-legs" tabindex="-1"><a class="header-anchor" href="#installing-led-strip-and-legs" aria-hidden="true">#</a> Installing LED strip and legs</h2><ol><li><p>Assemble the hoop for the LED strip by locking the ends together.</p><img src="'+zt+'" width="300" class="zoom border center"></li><li><p>Solder the JST male to the power pad and the Dupont-female to the signal pad.</p><img src="'+xt+'" width="300" class="zoom border center"></li><li><p>Stick the LED strip to the hoop, for greater strength, tighten it with 3-4 clamps.</p><img src="'+vt+'" width="300" class="zoom border center"></li><li><p>Install the feet on the stiffener plate with self-locking nuts and M3x8 screws only using the outermost mounting holes. From below, between the plates of the legs, install a damper silicone ring.</p><img src="'+kt+'" width="300" class="zoom border center"></li><li><p>Bend the legs back and install the hoop with the LED strip in the special groove on them, so that the connection cables exit from the tail side of the copter.</p><img src="'+yt+'" width="300" class="zoom border center"></li><li><p>Secure the legs with self-locking nuts and M3x10 screws.</p><img src="'+It+'" width="300" class="zoom border center"></li><li><p>Connect the LED strip power to the JST 5V connector on the power distribution board.</p><img src="'+Tt+'" width="300" class="zoom border center"></li><li><p>Connect the signal output of the LED strip in the Raspberry Ri to pin <em>GPIO21</em>.</p><img src="'+Nt+'" width="300" class="zoom border center"></li><li><p>Install the mounting deck and secure it with M3x8 screws.</p><img src="'+Mt+'" width="300" class="zoom border center"></li></ol><h2 id="setting-protection" tabindex="-1"><a class="header-anchor" href="#setting-protection" aria-hidden="true">#</a> Setting protection</h2><ol><li><p>Assemble the lower level of protection using 40mm posts and M3x10 screws.</p><img src="'+St+'" width="300" class="zoom border center"></li><li><p>Assemble the upper layer of protection using the M3x10 screws.</p><img src="'+Ct+'" width="300" class="zoom border center"></li><li><p>Install the protection and fix it to the beams with self-locking nuts and M3x10 screws.</p><img src="'+Pt+'" width="300" class="zoom border center"></li><li><p>Connect the flight controller to your Raspberry Pi using the USB cable.</p><div class="image-group"><img src="'+Dt+'" width="300" class="zoom border"><img src="'+Et+'" width="300" class="zoom border"></div></li><li><p>Install the battery strap.</p><img src="'+Rt+'" width="300" class="zoom border center"></li></ol>',26),Ft={class:"custom-container tip"},Jt=t("p",{class:"custom-container-title"},"TIP",-1),Ot=e("The drone is assembled, next perform the "),Ut=e('"setup"'),Xt=e(" step.");function jt(Ht,Yt){const i=s("ExternalLinkIcon"),a=s("RouterLink");return l(),c(h,null,[Lt,t("p",null,[At,t("a",qt,[Vt,r(i)]),Gt]),Wt,t("div",Ft,[Jt,t("p",null,[Ot,r(a,{to:"/en/setup.html"},{default:d(()=>[Ut]),_:1}),Xt])])],64)}var se=n(Bt,[["render",jt],["__file","assemble_4_2_ws.html.vue"]]);export{se as default};
